import React, { useEffect,useState } from 'react';
import axios from "axios"
import {Card,Button} from  'react-bootstrap'




function Movies(){

    //  axios film

    const [Movie,setMovie]= useState([])
    const getMovie=()=> {
        axios.get('http://localhost:3004/posts').then((response) => {

            setMovie( response.data);

          });

    }
    useEffect(()=>{getMovie()},[])

   const deleteRow=(id, e)=>{
      axios.delete(`http://localhost:3004/posts/${id}`)  
        .then(response => {  
          console.log("response: delete", response);
        }) 
        .catch(err=> 
          console.log(err)
        );
      }

  return(
    <div  className="cart">
          {Movie.map((el) => (
            <div>
            <Card className="carte" style={{ width: '18rem' }}>
  <Card.Img className="img" variant="top" src={el.Images} />
  <Card.Body>
    <Card.Title className="titr"> {el.Title} </Card.Title>
    <Card.Text className="txt" >{el.Awards}</Card.Text>
    <Card.Link className="link" href="#"><i class="fas fa-pen"></i>
    </Card.Link>
    <Button className="btn btn-danger"  onClick={(e) => deleteRow(el.id, e)}>
      <i class="fas fa-trash"></i>
    {/* strat modal */}
    <>
      <Button variant="primary" onClick={handleShow}>
        Launch static backdrop modal
      </Button>

      <Modal
        show={show}
        onHide={handleClose}
        backdrop="static"
        keyboard={false}
      >
        <Modal.Header closeButton>
          <Modal.Title>Modal title</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          I will not close if you click outside me. Don't even try to press
          escape key.
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary">Understood</Button>
        </Modal.Footer>
      </Modal>
    </>


    {/* end modal */}
    </Button>
     </Card.Body>
</Card>
          </div>

          )

          )}


    </div>
   )

 }

export default Movies
